// ===== Ğ£ĞŸĞ ĞĞ’Ğ›Ğ•ĞĞ˜Ğ• Ğ˜Ğ“Ğ ĞĞœĞ˜ =====
function startGame(gameNumber) {
    currentGame = gameNumber;
    
    // Ğ¡ĞºÑ€Ñ‹Ñ‚ÑŒ Ğ²ÑĞµ ÑĞµĞºÑ†Ğ¸Ğ¸
    const sections = document.querySelectorAll('.game-section');
    sections.forEach(section => {
        section.classList.remove('active');
    });
    
    // ĞŸĞ¾ĞºĞ°Ğ·Ğ°Ñ‚ÑŒ Ğ²Ñ‹Ğ±Ñ€Ğ°Ğ½Ğ½ÑƒÑ ÑĞµĞºÑ†Ğ¸Ñ
    const targetSection = document.getElementById(`game-${gameNumber}`);
    if (targetSection) {
        targetSection.classList.add('active');
    }
    
    // Ğ˜Ğ½Ğ¸Ñ†Ğ¸Ğ°Ğ»Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ ĞºĞ¾Ğ½ĞºÑ€ĞµÑ‚Ğ½Ğ¾Ğ³Ğ¾ Ñ€ĞµĞ¶Ğ¸Ğ¼Ğ°
    switch(gameNumber) {
        case 1:
            loadRandomStory();
            initAnswersButton(1);
            break;
        case 2:
            showRandomOceanFact();
            break;
        case 3:
            initTypingGame();
            break;
        case 4:
            loadHistoricalFacts();
            break;
        case 5:
            loadScientificArticle();
            break;
        case 6:
            loadPoem();
            break;
        case 7:
            loadEnglishSentence();
            initAnswersButton(7);
            break;
        case 8:
            loadGapSentence();
            initAnswersButton(8);
            break;
        case 9:
            initSpeedReading();
            initAnswersButton(9);
            break;
    }
    
    // ĞĞ±Ğ½Ğ¾Ğ²Ğ¸Ñ‚ÑŒ ÑÑ‚Ğ°Ñ‚Ğ¸ÑÑ‚Ğ¸ĞºÑƒ
    if (!gameStats.gameProgress[gameNumber]) {
        gameStats.gameProgress[gameNumber] = { played: false, score: 0 };
        gameStats.gamesPlayed++;
        saveStats();
        updateStatsDisplay();
    }
}

function backToMenu() {
    currentGame = 0;
    document.querySelectorAll('.game-section').forEach(section => {
        section.classList.remove('active');
    });
    const mainMenu = document.getElementById('main-menu');
    if (mainMenu) {
        mainMenu.classList.add('active');
    }
    saveStats();
}

