function checkAnswers(gameNum) {
    const gameSection = document.getElementById(`game-${gameNum}`);
    if (!gameSection) return;
    
    const answers = gameSection.querySelectorAll('.answer-input');
    let total = answers.length;
    let answered = 0;
    const userAnswers = [];
    
    answers.forEach((answer, index) => {
        if (answer.value.trim().length >= 10) {
            answer.style.border = '2px solid #4CAF50';
            answered++;
            userAnswers.push({
                question: index + 1,
                answer: answer.value.trim(),
                isComplete: true
            });
        } else {
            answer.style.border = '2px solid #F44336';
            userAnswers.push({
                question: index + 1,
                answer: answer.value.trim(),
                isComplete: false
            });
        }
    });
    
    if (answered === total) {
        gameStats.points += 10;
        gameStats.gameProgress[gameNum] = { played: true, score: 10 };
        updateModeProgress(gameNum, 100);
        
        // Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹ Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»Ñ
        answerState[gameNum].answered = true;
        answerState[gameNum].answers = userAnswers;
        
        // ĞŸĞ¾ĞºĞ°Ğ·Ñ‹Ğ²Ğ°ĞµĞ¼ ĞºĞ½Ğ¾Ğ¿ĞºÑƒ "ĞŸĞ¾ĞºĞ°Ğ·Ğ°Ñ‚ÑŒ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹"
        updateShowAnswersButton(gameNum, true);
        
        alert(`ğŸ‰ ĞÑ‚Ğ»Ğ¸Ñ‡Ğ½Ğ¾! Ğ’ÑĞµ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹ ÑĞ´Ğ°Ğ½Ñ‹! +10 Ğ±Ğ°Ğ»Ğ»Ğ¾Ğ²\nĞ¢ĞµĞ¿ĞµÑ€ÑŒ Ğ²Ñ‹ Ğ¼Ğ¾Ğ¶ĞµÑ‚Ğµ Ğ¿Ğ¾ÑĞ¼Ğ¾Ñ‚Ñ€ĞµÑ‚ÑŒ Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ»ÑŒĞ½Ñ‹Ğµ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹.`);
    } else {
        alert(`Ğ¡Ğ´Ğ°Ğ½Ğ¾ ${answered} Ğ¸Ğ· ${total} Ğ¾Ñ‚Ğ²ĞµÑ‚Ğ¾Ğ².\nĞ—Ğ°Ğ¿Ğ¾Ğ»Ğ½Ğ¸Ñ‚Ğµ Ğ²ÑĞµ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹ Ğ¿Ğ¾Ğ»Ğ½Ğ¾ÑÑ‚ÑŒÑ (Ğ¼Ğ¸Ğ½Ğ¸Ğ¼ÑƒĞ¼ 10 ÑĞ¸Ğ¼Ğ²Ğ¾Ğ»Ğ¾Ğ²), Ñ‡Ñ‚Ğ¾Ğ±Ñ‹ ÑĞ´Ğ°Ñ‚ÑŒ Ñ‚ĞµÑÑ‚!`);
        return;
    }
    
    saveStats();
    updateStatsDisplay();
}

