    // Ğ•ÑĞ»Ğ¸ Ğ²ÑĞµ Ñ„Ğ°ĞºÑ‚Ñ‹ Ğ¸ÑĞ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ğ½Ñ‹, ÑĞ±Ñ€Ğ°ÑÑ‹Ğ²Ğ°ĞµĞ¼ ÑĞ¿Ğ¸ÑĞ¾Ğº
    if (usedHistoryIndexes.length >= historicalFacts.length) {
        usedHistoryIndexes = [];
    }
    
