function checkGaps() {
    const container = document.getElementById('gapText');
    if (!container) return;
    
    const gapInput = container.querySelector('.gap-input');
    const gapResult = container.querySelector('#gapResult');
    const currentIndex = usedGapIndexes[usedGapIndexes.length - 1];
    const correctAnswer = gapSentences[currentIndex].answer;
    
    if (!gapInput) return;
    
    const userAnswer = gapInput.value.trim();
    
    // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼, Ñ‡Ñ‚Ğ¾ Ğ¾Ñ‚Ğ²ĞµÑ‚ ÑĞ´Ğ°Ğ½ (Ğ½Ğµ Ğ¿ÑƒÑÑ‚Ğ¾Ğ¹)
    if (!userAnswer) {
        alert('Ğ’Ğ²ĞµĞ´Ğ¸Ñ‚Ğµ ÑĞ»Ğ¾Ğ²Ğ¾ Ğ² Ğ¿Ñ€Ğ¾Ğ¿ÑƒÑĞº, Ñ‡Ñ‚Ğ¾Ğ±Ñ‹ ÑĞ´Ğ°Ñ‚ÑŒ Ğ¾Ñ‚Ğ²ĞµÑ‚!');
        return;
    }
    
    if (gapResult) {
        gapResult.style.display = 'block';
    }
    
    if (userAnswer.toLowerCase() === correctAnswer.toLowerCase()) {
        gapInput.style.borderBottom = '2px solid #4CAF50';
        gameStats.points += 3;
        alert(`âœ… ĞÑ‚Ğ²ĞµÑ‚ ÑĞ´Ğ°Ğ½! ĞŸÑ€Ğ°Ğ²Ğ¸Ğ»ÑŒĞ½Ğ¾! +3 Ğ±Ğ°Ğ»Ğ»Ğ°\nĞ¢ĞµĞ¿ĞµÑ€ÑŒ Ğ²Ñ‹ Ğ¼Ğ¾Ğ¶ĞµÑ‚Ğµ Ğ¿Ğ¾ÑĞ¼Ğ¾Ñ‚Ñ€ĞµÑ‚ÑŒ Ğ²ÑĞµ Ğ²Ğ°Ñ€Ğ¸Ğ°Ğ½Ñ‚Ñ‹.`);
    } else {
        gapInput.style.borderBottom = '2px solid #F44336';
        gameStats.points += 1;
        alert(`ğŸ“ ĞÑ‚Ğ²ĞµÑ‚ ÑĞ´Ğ°Ğ½! ĞŸĞ¾Ñ‡Ñ‚Ğ¸ Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ»ÑŒĞ½Ğ¾! +1 Ğ±Ğ°Ğ»Ğ»\nĞ¢ĞµĞ¿ĞµÑ€ÑŒ Ğ²Ñ‹ Ğ¼Ğ¾Ğ¶ĞµÑ‚Ğµ Ğ¿Ğ¾ÑĞ¼Ğ¾Ñ‚Ñ€ĞµÑ‚ÑŒ Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ»ÑŒĞ½Ñ‹Ğ¹ Ğ¾Ñ‚Ğ²ĞµÑ‚.`);
    }
    
    // Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼ Ğ¾Ñ‚Ğ²ĞµÑ‚ Ğ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ĞµĞ»Ñ
    answerState[8].answered = true;
    answerState[8].answers = [{
        userAnswer: userAnswer,
        correctAnswer: correctAnswer,
        sentence: gapSentences[currentIndex].sentence
    }];
    
    // ĞŸĞ¾ĞºĞ°Ğ·Ñ‹Ğ²Ğ°ĞµĞ¼ ĞºĞ½Ğ¾Ğ¿ĞºÑƒ "ĞŸĞ¾ĞºĞ°Ğ·Ğ°Ñ‚ÑŒ Ğ¾Ñ‚Ğ²ĞµÑ‚Ñ‹"
    updateShowAnswersButton(8, true);
    
    saveStats();
    updateStatsDisplay();
}

